parameters.jsCode:
// Parse Slack message and extract command
const payload = $input.item.json;
const event = payload.event || {};
const text = event.text || payload.text || '';
const channel = event.channel || payload.channel || '';
const ts = event.ts || payload.ts || '';
const userId = event.user || payload.user || '';

// Ignore bot messages
if (event.bot_id || payload.bot_id) {
  return { skip: true };
}

// Helper function to sanitize filename
function sanitizeFilename(name) {
  return name
    .toLowerCase()
    .replace(/[^a-z0-9\s-]/g, '')
    .replace(/\s+/g, '-')
    .replace(/-+/g, '-')
    .trim();
}

// Initialize result
let commandType = 'unknown';
let projectName = '';
let description = '';
let content = '';

// Command detection with case-insensitive matching
const lines = text.split('\n');
const firstLine = lines[0] || '';

// Check for "Hot topic:"
if (/^hot topic:/i.test(firstLine)) {
  commandType = 'hot_topic';
  description = text.replace(/^hot topic:\s*/i, '').trim();
}
// Check for "New project:"
else if (/^new project:/i.test(firstLine)) {
  commandType = 'new_project';
  const match = text.match(/^new project:\s*(.+?)(?:\n([\s\S]*))?$/i);
  if (match) {
    projectName = match[1].trim();
    content = match[2]?.trim() || '';
  }
}
// Check for "Update [name]:"
else if (/^update\s+/i.test(firstLine)) {
  const match = text.match(/^update\s+(.+?):\s*([\s\S]+)$/i);
  if (match) {
    commandType = 'update_project';
    projectName = match[1].trim();
    content = match[2].trim();
  }
}
// Check for "Transcript:"
else if (/^transcript:/i.test(firstLine)) {
  const match = text.match(/^transcript:\s*(.+?)(?:\n([\s\S]*))?$/i);
  if (match) {
    commandType = 'transcript';
    projectName = match[1].trim();
    content = match[2]?.trim() || text;
  }
}

// Get current date
const now = new Date();
const dateStr = now.toISOString().split('T')[0];
const timestamp = now.toISOString().replace('T', ' ').split('.')[0];

return {
  commandType,
  projectName,
  description,
  content,
  filename: projectName ? sanitizeFilename(projectName) : '',
  dateStr,
  timestamp,
  originalText: text,
  channel,
  threadTs: ts,
  userId
};

parameters.command:
=ssh -o StrictHostKeyChecking=no root@192.168.1.18 "if [ ! -f /context/briefs/{{ $json.filename }}.md ]; then echo 'ERROR: Brief not found'; exit 1; fi; cat >> /context/briefs/{{ $json.filename }}.md << 'EOF'

---
**Update:** {{ $json.timestamp }}

{{ $json.content }}
EOF
echo 'SUCCESS'"

=ssh -o StrictHostKeyChecking=no root@192.168.1.18 "cat > /context/transcripts/{{ $json.dateStr }}-{{ $json.filename }}.md << 'EOF'
# Transcript: {{ $json.projectName }}
**Date:** {{ $json.dateStr }}

{{ $json.content }}
EOF
chmod 644 /context/transcripts/{{ $json.dateStr }}-{{ $json.filename }}.md; echo 'SUCCESS'"

parameters.jsCode:
// Format success message
const input = $input.item.json;
let message = '';

if (input.commandType === 'hot_topic') {
  message = '✅ Added hot topic to /context/hot_topics.md';
} else if (input.commandType === 'new_project') {
  message = `✅ Created brief: /context/briefs/${input.filename}.md`;
} else if (input.commandType === 'update_project') {
  message = `✅ Updated brief: /context/briefs/${input.filename}.md`;
} else if (input.commandType === 'transcript') {
  message = `✅ Saved transcript: /context/transcripts/${input.dateStr}-${input.filename}.md`;
}

return { ...input, message };

// Format error message for unknown command
const input = $input.item.json;
const message = `❌ Command not recognized. Valid formats:
• New project: [name]
• Update [name]: [changes]
• Hot topic: [description]
• Transcript: [meeting]`;

return { ...input, message };
