// Parse Slack slash command payload
const payload = $input.item.json;

// Slack slash commands send form data
const command = payload.command || '';
const text = payload.text || '';
const userName = payload.user_name || '';
const channelName = payload.channel_name || '';
const responseUrl = payload.response_url || '';

// Helper function to sanitize filename
function sanitizeFilename(name) {
  return name.toLowerCase().replace(/[^a-z0-9\s-]/g, '').replace(/\s+/g, '-').replace(/-+/g, '-').trim();
}

// Initialize result
let commandType = 'unknown';
let projectName = '';
let description = '';
let content = '';

// Parse based on slash command
if (command === '/interests') {
  commandType = 'interests';
  description = text.trim();
}
else if (command === '/newproject') {
  commandType = 'new_project';
  const lines = text.split('\n');
  projectName = lines[0]?.trim() || '';
  content = lines.slice(1).join('\n').trim();
}

// Get current date
const now = new Date();
const dateStr = now.toISOString().split('T')[0];
const timestamp = now.toISOString().replace('T', ' ').split('.')[0];

return {
  commandType,
  projectName,
  description,
  content,
  filename: projectName ? sanitizeFilename(projectName) : '',
  dateStr,
  timestamp,
  userName,
  channelName,
  responseUrl,
  originalCommand: command,
  originalText: text
};

const input = $input.item.json;
let message = '';

if (input.commandType === 'interests') {
  message = `✅ Added interest to /context/hot_topics.md`;
} else if (input.commandType === 'new_project') {
  message = `✅ Created brief: /context/briefs/${input.filename}.md`;
}

return { text: message, response_type: 'ephemeral' };

const input = $input.item.json;
const message = `❌ Unknown command: ${input.originalCommand}`;
return { text: message, response_type: 'ephemeral' };
